import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';

const indexDownDescription = new GestureDescription('index_down');

// thumb:
indexDownDescription.addCurl(Finger.Thumb, FingerCurl.HalfCurl, 1.0);
//indexDownDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.25);
indexDownDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalLeft, 1.0);
indexDownDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalRight, 1.0);
indexDownDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 1.0);
indexDownDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 1.0);

// index:
indexDownDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.25);
indexDownDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 1.0);
indexDownDescription.addCurl(Finger.Index, FingerCurl.FullCurl, 1.0);

//others
for(let finger of [Finger.Middle, Finger.Ring, Finger.Pinky]) {
  indexDownDescription.addCurl(finger, FingerCurl.NoCurl, 1.0);
  indexDownDescription.addDirection(finger, FingerDirection.DiagonalUpLeft, 1.0);
  indexDownDescription.addDirection(finger, FingerDirection.DiagonalUpRight, 1.0);
}


export default indexDownDescription;
